input = ''
  .split('')

i = 0
marker_detected = false

while i < input.length - 3 && marker_detected == false do
  chars = [input[i], input[i + 1], input[i + 2], input[i + 3]]
  marker_detected = true if chars.length == chars.uniq.length
  i += 1
end

pp "The first time a marker appears is after character number #{i + 3}"
