input = ''
  .split('')

i = 0
marker_detected = false

while i < input.length - 3 && marker_detected == false do
  chars = [
    input[i],
    input[i + 1],
    input[i + 2],
    input[i + 3],
    input[i + 4],
    input[i + 5],
    input[i + 6],
    input[i + 7],
    input[i + 8],
    input[i + 9],
    input[i + 10],
    input[i + 11],
    input[i + 12],
    input[i + 13]
  ]
  marker_detected = true if chars.length == chars.uniq.length
  i += 1
end

pp "The first time a start-of-message marker appears is after character number #{i + 13}"
